import React, { useState, useRef, useEffect } from 'react';
import axios from 'axios';
import ReactMarkdown from 'react-markdown';
import { PaperAirplaneIcon, UserCircleIcon, Cog6ToothIcon, ExclamationCircleIcon } from '@heroicons/react/24/solid';

function App() {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isLoading, setIsLoading] = useState(false);
  const messagesEndRef = useRef(null);
  const [patientId, setPatientId] = useState('67e15c7763ec5e8bd20d8192'); // Default patient ID
  const [userId, setUserId] = useState('');
  const [showSettings, setShowSettings] = useState(false);
  const [error, setError] = useState(null);

  const scrollToBottom = () => {
    messagesEndRef.current?.scrollIntoView({ behavior: "smooth" });
  };

  useEffect(() => {
    scrollToBottom();
  }, [messages]);

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!input.trim()) return;

    const userMessage = input;
    setInput('');
    setError(null);
    setMessages(prev => [...prev, { type: 'user', content: userMessage }]);
    setIsLoading(true);

    try {
      const response = await axios.post('http://localhost:8000/chat', {
        message: userMessage,
        patient_id: patientId || undefined,
        user_id: userId || undefined,
        generate_report: false
      });

      setMessages(prev => [...prev, { type: 'assistant', content: response.data.response }]);
    } catch (error) {
      console.error('Error details:', error.response || error);
      const errorMessage = error.response?.data?.detail || error.message || 'An unexpected error occurred';
      setError(errorMessage);
      setMessages(prev => [...prev, {
        type: 'error',
        content: `Error: ${errorMessage}`
      }]);
    } finally {
      setIsLoading(false);
    }
  };

  // Error notification component
  const ErrorNotification = ({ message }) => (
    <div className="fixed top-4 right-4 bg-red-50 border-l-4 border-red-400 p-4 rounded shadow-lg">
      <div className="flex items-center">
        <ExclamationCircleIcon className="h-5 w-5 text-red-400 mr-2" />
        <p className="text-sm text-red-700">{message}</p>
      </div>
    </div>
  );

  return (
    <div className="flex h-screen bg-gray-50">
      {error && <ErrorNotification message={error} />}
      
      {/* Sidebar */}
      <div className={`fixed md:relative z-10 h-full w-64 sidebar transform ${showSettings ? 'translate-x-0' : '-translate-x-full'} md:translate-x-0 transition-transform duration-200 ease-in-out shadow-lg md:shadow-none`}>
        <div className="h-full glass-morphism p-6 space-y-6">
          <div className="flex items-center justify-between">
            <h2 className="text-xl font-semibold text-gray-800">Medical Assistant</h2>
            <button 
              onClick={() => setShowSettings(false)}
              className="md:hidden text-gray-500 hover:text-gray-700"
            >
              ×
            </button>
          </div>
          
          <div className="space-y-6">
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Patient ID</label>
              <input
                type="text"
                value={patientId}
                onChange={(e) => setPatientId(e.target.value)}
                className="input-field w-full px-4 py-2 rounded-lg bg-white bg-opacity-50 focus:bg-opacity-100"
                placeholder="Enter patient ID"
              />
              <p className="mt-1 text-xs text-gray-500">Default: 67e15c7763ec5e8bd20d8192</p>
            </div>
            <div>
              <label className="block text-sm font-medium text-gray-700 mb-2">Doctor ID</label>
              <input
                type="text"
                value={userId}
                onChange={(e) => setUserId(e.target.value)}
                className="input-field w-full px-4 py-2 rounded-lg bg-white bg-opacity-50 focus:bg-opacity-100"
                placeholder="Enter doctor ID"
              />
            </div>
          </div>
        </div>
      </div>

      {/* Main Chat Area */}
      <div className="flex-1 flex flex-col">
        {/* Header */}
        <div className="glass-morphism border-b px-6 py-4 flex items-center justify-between">
          <h1 className="text-xl font-semibold text-gray-800">Medical Chat Assistant</h1>
          <button
            onClick={() => setShowSettings(!showSettings)}
            className="md:hidden p-2 rounded-lg hover:bg-gray-100"
          >
            <Cog6ToothIcon className="h-6 w-6 text-gray-600" />
          </button>
        </div>

        {/* Messages */}
        <div className="flex-1 overflow-y-auto p-6 space-y-6 chat-container">
          {messages.map((message, index) => (
            <div
              key={index}
              className={`flex ${message.type === 'user' ? 'justify-end' : 'justify-start'}`}
            >
              <div
                className={`message-bubble flex max-w-[80%] ${
                  message.type === 'user'
                    ? 'bg-primary-500 text-white'
                    : message.type === 'error'
                    ? 'bg-red-500 text-white'
                    : 'glass-morphism'
                } rounded-2xl px-6 py-4`}
              >
                {message.type !== 'user' && (
                  <UserCircleIcon className="h-6 w-6 text-primary-600 mr-3 flex-shrink-0" />
                )}
                <div className={`prose prose-sm ${message.type === 'user' ? 'text-white' : 'text-gray-800'}`}>
                  <ReactMarkdown>{message.content}</ReactMarkdown>
                </div>
              </div>
            </div>
          ))}
          {isLoading && (
            <div className="flex justify-start">
              <div className="message-bubble glass-morphism rounded-2xl px-6 py-4">
                <div className="flex items-center">
                  <UserCircleIcon className="h-6 w-6 text-primary-600 mr-3" />
                  <div className="typing-indicator flex">
                    <div className="h-2 w-2"></div>
                    <div className="h-2 w-2"></div>
                    <div className="h-2 w-2"></div>
                  </div>
                </div>
              </div>
            </div>
          )}
          <div ref={messagesEndRef} />
        </div>

        {/* Input Area */}
        <div className="glass-morphism border-t p-6">
          <form onSubmit={handleSubmit} className="flex space-x-4">
            <input
              type="text"
              value={input}
              onChange={(e) => setInput(e.target.value)}
              placeholder="Type your message..."
              className="input-field flex-1 px-4 py-3 rounded-xl bg-white bg-opacity-50 focus:bg-opacity-100"
            />
            <button
              type="submit"
              disabled={isLoading}
              className="send-button px-6 py-3 rounded-xl bg-primary-500 text-white hover:bg-primary-600 focus:outline-none focus:ring-2 focus:ring-primary-500 focus:ring-offset-2"
            >
              <PaperAirplaneIcon className="h-5 w-5" />
            </button>
          </form>
        </div>
      </div>
    </div>
  );
}

export default App;
